
public class For9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i =2; i<=19; i++) { //for i 여는 부분
			System.out.println(i + "단을 출력합니다.");
			for(int j =1; j<=19;j++) { // for j 여는 부분 
			System.out.println(i + "*" + j + "=" + i*j);
			} // for j 닫는 부분 
		} // for i 닫는 부분
	} //main 닫는 부분
}
